import http from 'http'
import https from 'https'
import webOutgoing from './web-outgoing'
import * as common from '../common'


/*!
 * Array of passes.
 *
 * A `pass` is just a function that is executed on `req, res, options`
 * so that you can easily add new checks while still keeping the base
 * flexible.
 */


  /**
   * Sets `content-length` to '0' if request is of DELETE type.
   *
   * @param {ClientRequest} Req Request object
   * @param {IncomingMessage} Res Response object
   * @param {Object} Options Config object passed to the proxy
   *
   * @api private
   */

export function deleteLength(req, res, options) {
  if((req.method === 'DELETE' || req.method === 'OPTIONS') && !req.headers['content-length']) {
    req.headers['content-length'] = '0'
    delete req.headers['transfer-encoding']
  }
}

  /**
   * Sets timeout in request socket if it was specified in options.
   *
   * @param {ClientRequest} Req Request object
   * @param {IncomingMessage} Res Response object
   * @param {Object} Options Config object passed to the proxy
   *
   * @api private
   */
export function timeout(req, res, options) {
  if(options.timeout) req.socket.setTimeout(options.timeout)
}

  /**
   * Sets `x-forwarded-*` headers if specified in config.
   *
   * @param {ClientRequest} Req Request object
   * @param {IncomingMessage} Res Response object
   * @param {Object} Options Config object passed to the proxy
   *
   * @api private
   */
export function XHeaders(req, res, options) {
  if(!options.xfwd) return

  var encrypted = req.isSpdy || common.hasEncryptedConnection(req)
  var values =  { for: req.connection.remoteAddress || req.socket.remoteAddress
                , port: common.getPort(req)
                , proto: encrypted ? 'https' : 'http'
                }

  const xValues = [ 'for', 'port', 'proto' ]
  xValues.forEach(function setXForwardedHeader(header) {
    req.headers[`x-forwarded-${header}`] = `${req.headers[`x-forwarded-${header}`] || ''}${req.headers[`x-forwarded-${header}`] ? ',' : ''}${values[header]}`
  })
  try {
    var hostHeader = req.headers['host']
    if(hostHeader)
      req.headers['x-forwarded-host'] = hostHeader
    else
      console.warn('No host was specified in proxy request.')
  } catch(err) {
    console.error(err, 'An error occurred in xfwd area attempting to set proxy headers.')
  }
}

  /**
   * Does the actual proxying. If `forward` is enabled fires up
   * a ForwardStream, same happens for ProxyStream. The request
   * just dies otherwise.
   *
   * @param {ClientRequest} Req Request object
   * @param {IncomingMessage} Res Response object
   * @param {Object} Options Config object passed to the proxy
   *
   * @api private
   */

export function stream(req, res, options, _, server, clb) {

  // And we begin!
  server.emit('start', req, res, options.target)
  if(options.forward) {
    // If forward enable, so just pipe the request
    var forwardReq = (options.forward.protocol === 'https:' ? https : http).request(
      common.setupOutgoing(options.ssl || {}, options, req, 'forward')
    );
    (options.buffer || req).pipe(forwardReq)
    if(!options.target) { return res.end() }
  }

  // Request initalization
  var proxyReq = (options.target.protocol === 'https:' ? https : http).request(
    common.setupOutgoing(options.ssl || {}, options, req)
  )

  // Enable developers to modify the proxyReq before headers are sent
  proxyReq.on('socket', function(socket) {
    if(server) { server.emit('proxyReq', proxyReq, req, res, options) }
  })

  // allow outgoing socket to timeout so that we could
  // show an error page at the initial request
  if(options.proxyTimeout) {
    proxyReq.setTimeout(options.proxyTimeout, function() {
       proxyReq.abort()
    })
  }

  // Ensure we abort proxy if request is aborted
  req.on('aborted', function () {
    proxyReq.abort()
  })

  // Handle errors on incoming request as well as it makes sense to
  req.on('error', proxyError)

  // Error Handler
  proxyReq.on('error', proxyError)

  function proxyError (err){
    if (req.socket.destroyed && err.code === 'ECONNRESET') {
      server.emit('econnreset', err, req, res, options.target)
      return proxyReq.abort()
    }

    if (clb) {
      clb(err, req, res, options.target)
    } else {
      server.emit('error', err, req, res, options.target)
    }
  }

  (options.buffer || req).pipe(proxyReq)

  proxyReq.on('response', function(proxyRes) {
    if(server) { server.emit('proxyRes', proxyRes, req, res) }
    for(let outgoing of webOutgoing) {
      if(outgoing(req, res, proxyRes, options))
        break
    }

    // Allow us to listen when the proxy has completed
    proxyRes.on('end', function () {
      server.emit('end', req, res, proxyRes)
    })

    proxyRes.pipe(res)
  })

  //proxyReq.end();
}

import url from 'url'

const redirectRegex = /^30(1|2|7|8)$/

/*!
 * Array of passes.
 *
 * A `pass` is just a function that is executed on `req, res, options`
 * so that you can easily add new checks while still keeping the base
 * flexible.
 */

/**
 * If is a HTTP 1.0 request, remove chunk headers
 *
 * @param {ClientRequest} Req Request object
 * @param {IncomingMessage} Res Response object
 * @param {proxyResponse} Res Response object from the proxy request
 *
 * @api private
 */
export function removeChunked(req, res, proxyRes) {
  if (req.httpVersion === '1.0')
    delete proxyRes.headers['transfer-encoding']
}

/**
 * If is a HTTP 1.0 request, set the correct connection header
 * or if connection header not present, then use `keep-alive`
 *
 * @param {ClientRequest} Req Request object
 * @param {IncomingMessage} Res Response object
 * @param {proxyResponse} Res Response object from the proxy request
 *
 * @api private
 */
export function setConnection(req, res, proxyRes) {
  if (req.httpVersion === '1.0') {
    proxyRes.headers.connection = req.headers.connection || 'close'
  } else if (!proxyRes.headers.connection) {
    proxyRes.headers.connection = req.headers.connection || 'keep-alive'
  }
}

export function setRedirectHostRewrite(req, res, proxyRes, options) {
  if ((options.hostRewrite || options.autoRewrite || options.protocolRewrite)
      && proxyRes.headers['location']
      && redirectRegex.test(proxyRes.statusCode)) {
    var target = url.parse(options.target)
    var u = url.parse(proxyRes.headers['location'])

    // make sure the redirected host matches the target host before rewriting
    if (target.host != u.host) {
      return
    }

    if (options.hostRewrite) {
      u.host = options.hostRewrite
    } else if (options.autoRewrite) {
      u.host = req.headers['host']
    }
    if (options.protocolRewrite) {
      u.protocol = options.protocolRewrite
    }

    proxyRes.headers['location'] = u.format()
  }
}
/**
 * Copy headers from proxyResponse to response
 * set each header in response object.
 *
 * @param {ClientRequest} Req Request object
 * @param {IncomingMessage} Res Response object
 * @param {proxyResponse} Res Response object from the proxy request
 *
 * @api private
 */
export function writeHeaders(req, res, proxyRes) {
  for(let key of Object.keys(proxyRes.headers)) {
    if(typeof proxyRes.headers[key] !== 'undefined') {
      if(res.headersSent)
        throw new Error(`Cannot write header ${key} because headers have already been sent.`)
      res.setHeader(String(key).trim(), proxyRes.headers[key])
    }
  }
}

/**
 * Set the statusCode from the proxyResponse
 *
 * @param {ClientRequest} Req Request object
 * @param {IncomingMessage} Res Response object
 * @param {proxyResponse} Res Response object from the proxy request
 *
 * @api private
 */
export const writeStatusCode = (req, res, proxyRes) => res.writeHead(proxyRes.statusCode)


export default [ removeChunked, setConnection, setRedirectHostRewrite, writeHeaders, writeStatusCode ]

import http from 'http'
import https from 'https'
import * as common from '../common'

/*!
 * Array of passes.
 *
 * A `pass` is just a function that is executed on `req, socket, options`
 * so that you can easily add new checks while still keeping the base
 * flexible.
 */

/*
 * Websockets Passes
 *
 */

  /**
   * WebSocket requests must have the `GET` method and
   * the `upgrade:websocket` header
   *
   * @param {ClientRequest} Req Request object
   * @param {Socket} Websocket
   *
   * @api private
   */

export function checkMethodAndHeader (req, socket) {
  if (req.method !== 'GET' || !req.headers.upgrade) {
    socket.destroy()
    return true
  }

  if (req.headers.upgrade.toLowerCase() !== 'websocket') {
    socket.destroy()
    return true
  }
}

/**
 * Sets `x-forwarded-*` headers if specified in config.
 *
 * @param {ClientRequest} Req Request object
 * @param {Socket} Websocket
 * @param {Object} Options Config object passed to the proxy
 *
 * @api private
 */
export function XHeaders(req, socket, options) {
  if(!options.xfwd) return

  var values =  { for: req.connection.remoteAddress || req.socket.remoteAddress
                , port: common.getPort(req)
                , proto: common.hasEncryptedConnection(req) ? 'wss' : 'ws'
                }

  const xValues = [ 'for', 'port', 'proto' ]
  xValues.forEach(header => {
    req.headers[`x-forwarded-${header}`] = `${req.headers[`x-forwarded-${header}`] || ''}${req.headers[`x-forwarded-${header}`] ? ',' : ''}${values[header]}`
  })
}

/**
 * Does the actual proxying. Make the request and upgrade it
 * send the Switching Protocols request and pipe the sockets.
 *
 * @param {ClientRequest} Req Request object
 * @param {Socket} Websocket
 * @param {Object} Options Config object passed to the proxy
 *
 * @api private
 */
export function stream(req, socket, options, head, server, clb) {
  common.setupSocket(socket)

  if (head && head.length) socket.unshift(head)


  var proxyReq = (common.isSSL.test(options.target.protocol) ? https : http).request(
    common.setupOutgoing(options.ssl || {}, options, req)
  )

  // Enable developers to modify the proxyReq before headers are sent
  if (server) { server.emit('proxyReqWs', proxyReq, req, socket, options, head) }

  // Error Handler
  proxyReq.on('error', onOutgoingError)
  proxyReq.on('response', function (res) {
    // if upgrade event isn't going to happen, close the socket
    if (!res.upgrade) socket.end()
  })

  proxyReq.on('upgrade', function(proxyRes, proxySocket, proxyHead) {
    proxySocket.on('error', onOutgoingError)

    // Allow us to listen when the websocket has completed
    proxySocket.on('end', function () {
      server.emit('close', proxyRes, proxySocket, proxyHead)
    })

    // The pipe below will end proxySocket if socket closes cleanly, but not
    // if it errors (eg, vanishes from the net and starts returning
    // EHOSTUNREACH). We need to do that explicitly.
    socket.on('error', function () {
      proxySocket.end()
    })

    common.setupSocket(proxySocket)

    if (proxyHead && proxyHead.length) proxySocket.unshift(proxyHead)

    //
    // Remark: Handle writing the headers to the socket when switching protocols
    // Also handles when a header is an array
    //
    socket.write(
      Object.keys(proxyRes.headers).reduce(function (head, key) {
        var value = proxyRes.headers[key]

        if (!Array.isArray(value)) {
          head.push(key + ': ' + value)
          return head
        }

        for (var i = 0; i < value.length; i++) {
          head.push(key + ': ' + value[i])
        }
        return head
      }, [ 'HTTP/1.1 101 Switching Protocols' ])
      .join('\r\n') + '\r\n\r\n'
    )

    proxySocket.pipe(socket).pipe(proxySocket)

    server.emit('open', proxySocket)
    server.emit('proxySocket', proxySocket)  //DEPRECATED.
  })

  return proxyReq.end() // XXX: CHECK IF THIS IS THIS CORRECT

  function onOutgoingError(err) {
    if (clb) {
      clb(err, req, socket)
    } else {
      server.emit('error', err, req, socket)
    }
    socket.end()
  }
}

import { inherits } from 'util'
import { parse as parse_url } from 'url'
import EE3 from 'eventemitter3'
import http from 'http'
import https from 'https'
import * as web from './passes/web-incoming'
import * as ws from './passes/ws-incoming'

/**
 * Returns a function that creates the loader for
 * either `ws` or `web`'s  passes.
 *
 * Examples:
 *
 *    httpProxy.createRightProxy('ws')
 *    // => [Function]
 *
 * @param {String} Type Either 'ws' or 'web'
 * 
 * @return {Function} Loader Function that when called returns an iterator for the right passes
 *
 * @api private
 */

export function createRightProxy(type) {

  return function(options) {
    return function(req, res /*, [head], [opts] */) {
      let passes = (type === 'ws') ? this.wsPasses : this.webPasses
      let args = [].slice.call(arguments)
      let cntr = args.length - 1
      let head
      let cbl

      /* optional args parse begin */
      if(typeof args[cntr] === 'function') {
        cbl = args[cntr]
        cntr--
      }

      if(
        !(args[cntr] instanceof Buffer) &&
        args[cntr] !== res
      ) {
        //Overwrite with request options
        options = { ...options, ...args[cntr] }
        cntr--
      }

      if(args[cntr] instanceof Buffer)
        head = args[cntr]

      /* optional args parse end */
      const argKeys = [ 'target', 'forward' ]
      argKeys.forEach(e => {
        if (typeof options[e] === 'string')
          options[e] = parse_url(options[e])
      })

      if (!options.target && !options.forward)
        return this.emit('error', new Error('Must provide a proper URL as target'))

      for(var i=0; i < passes.length; i++) {
        /**
         * Call of passes functions
         * pass(req, res, options, head)
         *
         * In WebSockets case the `res` variable
         * refer to the connection socket
         * pass(req, socket, options, head)
         */
        if(passes[i](req, res, options, head, this, cbl)) { // passes can return a truthy value to halt the loop
          break
        }
      }
    }
  }
}

export default class ProxyServer extends EE3 {
  constructor(options) {
    super(options)
    options = options || {}
    options.prependPath = options.prependPath === false ? false : true

    this.web = this.proxyRequest           = createRightProxy('web')(options)
    this.ws  = this.proxyWebsocketRequest  = createRightProxy('ws')(options)
    this.options = options

    this.webPasses = Object.keys(web).map(pass => web[pass])
    this.wsPasses = Object.keys(ws).map(pass => ws[pass])
    this.on('error', this.onError, this)
  }
  onError = err => {
    //
    // Remark: Replicate node core behavior using EE3
    // so we force people to handle their own errors
    //
    if(this.listeners('error').length === 1)
      throw err
  };

  listen = (port, hostname) => {
    const closure = (req, res) => { this.web(req, res) }
    this._server  = this.options.ssl ?
      https.createServer(this.options.ssl, closure) :
      http.createServer(closure)

    if(this.options.ws)
      this._server.on('upgrade', (req, socket, head) => { this.ws(req, socket, head) })
    this._server.listen(port, hostname)
    return this
  };

  close = callback => {
    if (this._server) {
      this._server.close(() => {
        this._server = null
        if (callback)
          callback.apply(null, arguments)
      })
    }
  };

  before = (type, passName, callback) => {
    if (type !== 'ws' && type !== 'web')
      throw new Error('type must be `web` or `ws`')
    let passes = (type === 'ws') ? this.wsPasses : this.webPasses
    let i = false

    passes.forEach((v, idx) => { if(v.name === passName) i = idx })

    if(i === false) throw new Error('No such pass')
    passes.splice(i, 0, callback)
  };

  after = (type, passName, callback) => {
    if (type !== 'ws' && type !== 'web')
      throw new Error('type must be `web` or `ws`')
    let passes = (type === 'ws') ? this.wsPasses : this.webPasses
    let i = false

    passes.forEach((v, idx) => {
      if(v.name === passName) i = idx
    })

    if(i === false) throw new Error('No such pass')

    passes.splice(i++, 0, callback)
  };
}
